import { useState, useEffect } from 'react';
import './Chat.css'
import ChatInfo from './Chat_info';
import Chat from './Chat';
import ReactDOM from 'react-dom/client';
import { useNavigate } from 'react-router-dom';

function Chats(){

    const navigate = useNavigate()

    const[search, setSearch] = useState('')
    const[searchCheck, setSearchCheck] = useState(true)
    const[isClick, setIsClick] = useState(false)
    const[userInfo, setUserInfo] = useState({username:'', id:-1})


    const handleChange = (e) => {
      setSearch(e.target.value)
      
    }

    async function refersh() {
      try {
        const response = await fetch("http://localhost:8080/auth/refresh", {
          method: "POST",
          credentials: "include", 
          headers: {
            "Content-Type": "application/json"
          }
        });

        if (response.ok) {
          const data = await response.text();
          setUserInfo((prev) => [...prev, data])
          console.log(userInfo)
        } else {
          navigate('/')
        }
      } catch (error) {
        navigate('/')
      }
    }

    async function searchUser(){
      if(e.target.value.trim()==''){
        setSearchCheck(true)
      }
      else{
        setSearchCheck(false)
      }
      try {
      const response = await fetch(`http://localhost:8080/api/users/${search}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem("token")}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.ok) {
        alert("Круто")
        setUserInfo(await response.text())
        console.log(userInfo)
      } 
      else if (response.status === 401) {
        refersh()
      }
      else {
        alert('Ошибка при отправке данных');
        console.log(response.status)
      } 
      } catch (error) {
        console.error('Ошибка:', error);
      }
    }

    return(
        <div className='chats_block'>
            <div className="folder_block">
                <div className="user_block">
                    <div className="user_folder_photo"></div>
                    <input className="user_folder_search" placeholder='Поиск' value={search} onChange={handleChange}/>
                    <button onClick={searchUser}>src</button>
                </div>
                <div className="folder">
                    {/* {messangeinfo.map(item => 
                        <ChatInfo
                            name={item.name}
                            text = {item.text}
                            time={item.time}
                            img={item.img}
                            func={() => clickHandler(item.chatId)}
                        />
                    )} */}

                    {searchCheck ? 
                      // <ChatInfo
                      //     name={item.name}
                      //     text = {item.text}
                      //     time={item.time}
                      //     img={item.img}
                      //     func={() => clickHandler(item.chatId)}
                      // /> 
                      <div></div>
                      : 
                      <div>ssss</div>
                    }
                </div>
            </div>
            <div className='chat_block'>

              {isClick ? <Chat/> : <div></div>}
                <Chat
                    // id={chooseChat}
                />
                
            </div>
        </div>
    )
    
}

export default Chats;
