import { useState, useEffect } from 'react';
import './Chat.css'
import ChatInfo from './Chat_info';
import Chat from './Chat';
import ReactDOM from 'react-dom/client';
import { useNavigate } from 'react-router-dom';

function Chats(){

    const navigate = useNavigate()

    const[search, setSearch] = useState('')

    let check = true

    const handleChange = (e) => {
      setSearch(e.target.value)
      console.log(e.target.value)
      if(e.target.value.trim==''){
        check = true
      }
      else{
        check = false
      }
      console.log(check)
    }

    return(
        <div className='chats_block'>
            <div className="folder_block">
                <div className="user_block">
                    <div className="user_folder_photo"></div>
                    <input className="user_folder_search" placeholder='Поиск' value={search} onChange={handleChange}/>
                    <button>src</button>
                </div>
                <div className="folder">
                    {/* {messangeinfo.map(item => 
                        <ChatInfo
                            name={item.name}
                            text = {item.text}
                            time={item.time}
                            img={item.img}
                            func={() => clickHandler(item.chatId)}
                        />
                    )} */}

                    {check ? <div>ddd</div> : <div>ssss</div>}
                </div>
            </div>
            <div className='chat_block'>
                <Chat
                    // id={chooseChat}
                />
                
            </div>
        </div>
    )
    
}

export default Chats;
