import { useState } from 'react'
import './Forms.css'
function Login(){
    const [info, setInfo] = useState(
        {
            username: "",
            email: "",
            password: ""
        }
    )
    
    async function handle() {
        const res = await fetch("http://loacalhost:8080/auth/signin", {
            method: "POST",
            body: JSON.stringify(info),
            headers: {
                "Content-Type": "application/json"
            }
        })
    
        if(res.ok){
            const token = await res.text()
            localStorage.setItem("token", token)
            console.log(token)
            alert("NICE")


        }
    }
    return(
        <div className="forms_block">
            <form className="forms">
                <div className="main_form_text">Вход</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input"/>
                </div>
                <div className="form_block">
                    <label className="form_label">Пароль</label>
                    <input className="form_input"/>
                </div> 
                <button className="form_btn">Войти</button>
                <div className="little_form_text">Зарегестрироваться</div>
            </form>
        </div>
    )
}
export default Login;
