import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
import './Forms.css'

function Group(props){

    const [groupName, setGroupName] = useState('')
    const [groupDescription, setGroupDescription] = useState('')
    const [isLoading, setIsLoading] = useState(false)
    const [user, setUser] = useState([])
    const [userid, setuserId] = useState(-1)
    const [contacts, setContacts] = useState([])
    const [selectedContacts, setSelectedContacts] = useState([])
    const [searchQuery, setSearchQuery] = useState('')
    

    const navigate = useNavigate()

    const handleGroupName = (event) => {
        setGroupName(event.target.value)
    }

    const handleGroupDescription = (event) => {
        setGroupDescription(event.target.value)
    }

    const handleSearchQuery = (event) => {
        setSearchQuery(event.target.value)
    }

    const toggleContactSelection = (contactId) => {
        setSelectedContacts(prev => 
            prev.includes(contactId) 
                ? prev.filter(id => id !== contactId)
                : [...prev, contactId]
        )
    }

    async function refresh(){
        try {
        const response = await fetch("http://localhost:8080/auth/refresh", {
          method: "POST",
          credentials: "include", 
          headers: {
            "Content-Type": "application/json"
          },
        });

        if (response.ok) {
          const token = await response.text()
          localStorage.setItem("token", token)
        } else {
        
        }
      } catch (error) {
      
      }
    }

    const getUserId = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/users/id/${localStorage.getItem("token")}`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem("token")}`,
                    'Content-Type': 'application/json'
                },
            });
            
            if (response.ok) {
                const data = await response.json();
                setuserId(data)
                return data
            } else if (response.status === 401) {
                if (await refresh()) {
                    return await getUserId()
                }
            }
            return null
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞:', error);
            return null
        }
    }

    const fetchContacts = async () => {
        try {
            const response = await fetch(`http://localhost:8080/api/contacts`, {
                method: 'GET',
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem("token")}`,
                    'Content-Type': 'application/json'
                },
            });
            
            if (response.ok) {
                const data = await response.json();
                setContacts(data);
            } else if (response.status === 401) {
                if (await refresh()) {
                    await fetchContacts();
                }
            }
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –∫–æ–Ω—Ç–∞–∫—Ç–æ–≤:', error);
        }
    }

    async function createGroup () {
        if (!groupName.trim()) {
            alert('–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–≤–µ–¥–∏—Ç–µ –Ω–∞–∑–≤–∞–Ω–∏–µ –≥—Ä—É–ø–ø—ã');
            return;
        }

        setIsLoading(true);
        
        try {
        const response = await fetch(`http://localhost:8080/api/groups/create`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem("token")}`
            },
            body: JSON.stringify({
                groupName: groupName,
                groupDescription: groupDescription,
                creatorId: userid,
                memberIds: selectedContacts
            }),
        }
        );
        if(response.ok){
            await refresh()
            alert("–ì—Ä—É–ø–ø–∞ —É—Å–ø–µ—à–Ω–æ —Å–æ–∑–¥–∞–Ω–∞! üéâ")
            navigate('/chats')
        }
        else{
            alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –≥—Ä—É–ø–ø—ã")
        }
        } catch (error) {
            console.error('–û—à–∏–±–∫–∞:', error);
            alert("–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –≥—Ä—É–ø–ø—ã")
        } finally {
            setIsLoading(false);
        }
    }

    useEffect(() => {
        const init = async () => {
            const id = await getUserId()
            await fetchContacts()
        }
        init()
    }, [])

    function toChats(){
        navigate('/chats')
    }

    const filteredContacts = contacts.filter(contact => 
        contact.username.toLowerCase().includes(searchQuery.toLowerCase())
    )
    
    return(
        <div className="forms_block_settings">
            <div className="group-form-container">
                <div className="group-form-header">
                    <button className="group-form-close" onClick={toChats}>
                        <span className="close-icon">‚úï</span>
                    </button>
                    <div className="group-form-title">
                        <div className="group-form-icon">üë•</div>
