import { useState } from 'react';
import './Forms.css'
function Registration(){

    const [info, setInfo] = useState(
        {
            username: "",
            email: "",
            password: ""
        }
    )

    function changeInfo(e){
        const copy = {...info}
        copy[e.target.name] = e.target.value
        console.log(info)
        setInfo(copy)
    }

    async function handle() {
        const res = await fetch("http://loacalhost:8080/auth/signup", {
            method: "POST",
            body: JSON.stringify(info),
            headers: {
                "Content-Type": "application/json"
            }
        })

        if(res.ok){
            alert("NICE")
        }
    }

    return(
        <div className="forms_block">
            <form className="forms_register">
                <div className="main_form_text">Регистрация</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input" value={info.username} onChange={changeInfo}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Почта</label>
                    <input className="form_input" value={info.email} onChange={changeInfo}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Пароль</label>
                    <input className="form_input" value={info.password} onChange={changeInfo}/>
                </div> 
                <button className="form_btn">Создать</button>
                <button className="little_form_text" onClick={handle}>Войти</button>
            </form>
        </div>
    )
}

export default Registration;
