import { useState } from 'react';
import './Forms.css'
function Registration(){

    
    // const [name, setName] = useState('')
    // const [email, setEmail] = useState('')
    // const [password, setPassword] = useState('')

    // const handleChangeName = (event) => {
    //     setName(event.target.value)
        
    // }

    // const handleChangeEmail = (event) => {
    //     setEmail(event.target.value)
        
    // }

    // const handleChangePassword = (event) => {
    //     setPassword(event.target.value)
        
    // }

    // async function handle() {
    //     const res = fetch("http://loacalhost:8080/auth/signup", {
    //         method: "POST",
    //         body: JSON.stringify({
    //             username: name,
    //             email: email,
    //             password: password
    //         }),
    //         headers: {
    //             "Content-Type": "application/json",
    //         }
    //     })

    //     if(res.ok){
    //         alert("NICE")
    //     }
    //     else{
    //         alert("sasa")
    //     }
    // }

    const [formData, setFormData] = useState({
    name: '',
    email: '',
    password:''
  });

  const handleChange = (e) => {
    setFormData({ 
      ...formData, 
      [e.target.name]: e.target.value 
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (response.ok) {
        alert('Данные успешно отправлены!');
      } else {
        alert('Ошибка при отправке данных');
      }
    } catch (error) {
      console.error('Ошибка:', error);
    }
  };

    return(
        <div className="forms_block" onSubmit={handleSubmit}>
            <form className="forms_register">
                <div className="main_form_text">Регистрация</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input" value={formData.name} onChange={handleChange}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Почта</label>
                    <input className="form_input" value={formData.email} onChange={handleChange}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Пароль</label>
                    <input className="form_input" value={formData.password} onChange={handleChange}/>
                </div> 
                <button className="form_btn" >Создать</button>
                <button className="little_form_text">Войти</button>
            </form>
        </div>
    )
}

export default Registration;
