import { useState } from 'react'
import './Forms.css'
function Login(){
    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    let info = {
        name:"",
        email:"",
        password:""
    }

    const handleChangeName = (event) => {
        setName(event.target.value)
        info.name = event.target.value
    }

    const handleChangeEmail = (event) => {
        setEmail(event.target.value)
        info.email = event.target.value
    }

    const handleChangePassword = (event) => {
        setPassword(event.target.value)
        info.password = event.target.value
    }

    const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:8080/auth/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
            username:name,
            password:password
        }),
        mode: 'cors'
      });

      if(response.ok){
            const token = await response.text()
            localStorage.setItem("token", token)
            console.log(token)
            alert("NICE")
        }
        else{
            alert("redf")
        }
    } catch (error) {
      console.error('Ошибка:', error);
    }
  };

    async function handle() {
        const res = await fetch("http://loacalhost:8080/auth/signin", {
            method: "POST",
            body: JSON.stringify(info),
            headers: {
                "Content-Type": "application/json"
            }
        })
    
        if(res.ok){
            const token = await res.text()
            localStorage.setItem("token", token)
            console.log(token)
            alert("NICE")


        }
        else{
            alert("redf")
        }
    }
    return(
        <div className="forms_block">
            <form className="forms">
                <div className="main_form_text">Вход</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input" value = {name} onChange={handleChangeName}/>
                </div>
                <div className="form_block">
                    <label className="form_label" value={password} onChange={handleChangePassword}>Пароль</label>
                    <input className="form_input"/>
                </div> 
                <button className="form_btn" onClick={handle}>Войти</button>
                <div className="little_form_text">Зарегестрироваться</div>
            </form>
        </div>
    )
}
export default Login;
