import { useState } from "react";
import'./Chat.css'

function Chat(props){
    

    async function getAllMessage(id){
      try{
        const response = await fetch(`http://localhost:8080/api/chats/${await getId()}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem("token")}`,
            'Content-Type': 'application/json'
          },
        })

        if (response.ok){
          const data = await response.json()
          await setUserChats([data])
        }

        else {
          setSearchCheck(true)
          console.log(response.status)
        } 

      } catch(error){
        setSearchCheck(true)
        console.error('Ошибка:', error);
      }
    } 

    const [chooseChat, setchooseChat] = useState(0);
    function clickHandler(id){
        setchooseChat(id)
        console.log(id)
    }


    return(
        <div>

            <input type="text" className="input_chat_msg"/>
        </div>
    )
}
export default Chat;
