    return(
        <div className="forms_block">
            <form className="forms_register">
                <div className="main_form_text">Регистрация</div>
                <div className="form_block">
                    <label className="form_label">Имя</label>
                    <input className="form_input" value={info.username} onChange={handleChange}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Почта</label>
                    <input className="form_input" value={info.email} onChange={handleChange}/>
                </div>
                <div className="form_block">
                    <label className="form_label">Пароль</label>
                    <input className="form_input" value={info.password} onChange={handleChange}/>
                </div> 
                <button className="form_btn" onClick={handle}>Создать</button>
                <button className="little_form_text">Войти</button>
            </form>
        </div>
    )
}

export default Registration;
