import { useEffect, useState, useRef, useCallback } from "react";
import { Client } from '@stomp/stompjs';
import './Chat.css';

function Chat({ userId, user2Id, username }) {
  const [messages, setMessages] = useState([]);
  const [chatId, setChatId] = useState(null);
  const [inputText, setInputText] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const clientRef = useRef(null);
  const containerRef = useRef(null);

  // Получение или создание чата
  const fetchChatId = useCallback(async () => {
    try {
      const response = await fetch(
        `http://localhost:8080/api/chats/twoId?id1=${Math.min(userId, user2Id)}&id2=${Math.max(userId, user2Id)}`, 
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem("token")}`,
          }
        }
      );

      if (response.ok) {
        return await response.text();
      } else if (response.status === 404) {
        const createResponse = await fetch('http://localhost:8080/api/chats', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem("token")}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            userId: Math.min(userId, user2Id),
            user2Id: Math.max(userId, user2Id)
          })
        });
        
        if (createResponse.ok) {
          return await createResponse.text();
        }
        throw new Error('Failed to create chat');
      }
      throw new Error('Failed to get chat');
    } catch (error) {
      console.error('Chat error:', error);
      setError('Failed to initialize chat');
      return null;
    }
  }, [userId, user2Id]);

  // Загрузка сообщений
  const loadMessages = useCallback(async (id) => {
    setIsLoading(true);
    try {
      const response = await fetch(
        `http://localhost:8080/api/messages/chat/${id}/recent`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem("token")}`,
          }
        }
      );

      if (response.ok) {
        const data = await response.json();
        setMessages(data.reverse()); // Новые сообщения внизу
      } else {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
    } catch (error) {
      console.error("Error loading messages:", error);
      setError("Failed to load messages");
    } finally {
      setIsLoading(false);
    }
  }, []);

  // Инициализация WebSocket
  const initWebSocket = useCallback((id) => {
    const client = new Client({
      brokerURL: 'ws://localhost:8080/ws',
      reconnectDelay: 5000,
      heartbeatIncoming: 4000,
      heartbeatOutgoing: 4000,
      connectHeaders: {
        Authorization: `Bearer ${localStorage.getItem("token")}`
      },
      debug: function(str) {
        console.log('STOMP:', str);
      },
      onConnect: () => {
        setIsConnected(true);
        console.log("WebSocket connected");

        // Подписка на обновления чата
        client.subscribe(`/topic/chat/${id}`, (message) => {
          const msg = JSON.parse(message.body);
          setMessages(prev => [...prev, msg]);
        });

        // Подписка на ошибки
        client.subscribe(`/user/queue/errors`, (message) => {
          console.error('Error:', message.body);
          setError(message.body);
        });
      },
      onDisconnect: () => {
        setIsConnected(false);
        console.log("WebSocket disconnected");
      },
      onStompError: (frame) => {
        console.error('Broker error:', frame.headers['message']);
        setError(frame.headers['message'] || 'WebSocket error');
      }
    });

    client.activate();
    clientRef.current = client;

    return () => {
      if (client.connected) {
        client.deactivate();
      }
    };
  }, []);

  // Отправка сообщения
  const sendMessage = useCallback(() => {
    if (!inputText.trim() || !isConnected || !chatId) return;

    try {
      clientRef.current.publish({
        destination: `/app/chat.send`,
        body: JSON.stringify({
          senderId: userId,
          chatId: chatId,
          text: inputText,
          timestamp: new Date().toISOString()
        }),
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        }
      });
      setInputText('');
    } catch (error) {
      console.error("Error sending message:", error);
      setError("Failed to send message");
    }
  }, [inputText, isConnected, chatId, userId]);

  // Инициализация чата
  useEffect(() => {
    const initializeChat = async () => {
      const id = await fetchChatId();
      if (id) {
        setChatId(id);
        await loadMessages(id);
        initWebSocket(id);
      }
    };

    initializeChat();

    return () => {
      if (clientRef.current && clientRef.current.connected) {
        clientRef.current.deactivate();
      }
    };
  }, [fetchChatId, loadMessages, initWebSocket]);

  // Автоскролл при новых сообщениях
  useEffect(() => {
    if (containerRef.current && messages.length > 0) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [messages]);

  if (error) {
    return <div className="chat-error">Error: {error}</div>;
  }

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h3>Chat with {username}</h3>
        <div className={`connection-status ${isConnected ? 'connected' : 'disconnected'}`}>
          {isConnected ? 'Online' : 'Offline'}
        </div>
      </div>

      <div className="messages-container" ref={containerRef}>
        {isLoading ? (
          <div className="loading">Loading messages...</div>
        ) : (
          messages.map((msg, index) => (
            <div
              key={`${msg.id || index}`}
              className={`message ${msg.senderId === userId ? 'my-message' : 'their-message'}`}
            >
              <div className="message-content">{msg.text || msg.content}</div>
              <div className="message-time">
                {new Date(msg.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}
      </div>

      <div className="message-input">
        <input
          type="text"
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
          placeholder="Type a message..."
          disabled={!isConnected}
        />
        <button
          onClick={sendMessage}
          disabled={!inputText.trim() || !isConnected}
        >
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
