import { useEffect, useState, useRef } from "react";
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import './Chat.css';

function Chat(props) {
  const [messages, setMessages] = useState([]);
  const [chatId, setChatId] = useState(null);
  const [inputText, setInputText] = useState('');
  const [hasMore, setHasMore] = useState(true);

  const clientRef = useRef(null);
  const isFirstLoad = useRef(true);
  const containerRef = useRef(null);

  useEffect(() => {
    const getChatId = async () => {
      try {
        const response = await fetch(`http://localhost:8000/api/chats/twoId?id1=${props.userId}&id2=${props.user2Id}`, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem("token")}`,
          }
        });

        if (response.ok) {
          const id = await response.text();
          setChatId(id);
        } else {
          console.error('Ошибка при получении chatId');
        }
      } catch (error) {
        console.error('Ошибка:', error);
      }
    };

    getChatId();
  }, [props.userId, props.user2Id]);

  useEffect(() => {
    if (!chatId) return;

    const socket = new SockJS("http://localhost:8000/ws");
    const client = new Client({
      webSocketFactory: () => socket,
      reconnectDelay: 5000,
      onConnect: () => {
        console.log("WebSocket connected");

        client.subscribe(`/topic/chat/${chatId}`, (message) => {
          const msg = JSON.parse(message.body);
          setMessages((prev) => [...prev, msg]);
        });
      },
      onStompError: (frame) => {
        console.error('Broker error:', frame.headers['message']);
      }
    });

    client.activate();
    clientRef.current = client;

    return () => {
      client.deactivate();
    };
  }, [chatId]);

  useEffect(() => {
    if (chatId && isFirstLoad.current) {
      const loadLastMessages = async () => {
        try {
          const res = await fetch(`http://localhost:8080/api/messages/last/${chatId}`);
          if (res.ok) {
            const data = await res.json();
            setMessages(data);
            isFirstLoad.current = false;
          }
        } catch (error) {
          console.error("Ошибка загрузки сообщений:", error);
        }
      };

      loadLastMessages();
    }
  }, [chatId]);

  const loadMoreMessages = async () => {
    if (!hasMore || messages.length === 0) return;

    const oldestTimestamp = messages[0].timestamp;

    try {
      const res = await fetch(
        `http://localhost:8080/api/messages/load?chatId=${chatId}&before=${oldestTimestamp}`
      );
      if (res.ok) {
        const data = await res.json();
        if (data.length === 0) {
          setHasMore(false);
        } else {
          setMessages((prev) => [...data, ...prev]);
        }
      }
    } catch (error) {
      console.error("Ошибка подгрузки:", error);
    }
  };

  const handleScroll = () => {
    if (containerRef.current.scrollTop === 0) {
      loadMoreMessages();
    }
  };

  const sendMessage = () => {
    if (!inputText.trim() || !clientRef.current || !clientRef.current.connected) return;

    clientRef.current.publish({
      destination: `/app/${props.user2Id}/chat.send`,
      body: JSON.stringify({
        senderId: props.userId,
        recipientId: props.user2Id,
        chatId: chatId,
        text: inputText,
        timestamp: new Date().toISOString()
      }),
    });

    setInputText('');
  };

  return (
    <div>
      <div className="userInfo_chat">
        <div className="userName_chat">{props.username}</div>
      </div>

      <div
        className="messages-container"
        onScroll={handleScroll}
        ref={containerRef}
        style={{ overflowY: 'auto', maxHeight: '400px' }}
      >
        {messages.map((msg, index) => (
          <div
            key={index}
            className={msg.senderId === props.userId ? 'my-message' : 'their-message'}
          >
            {msg.text}
          </div>
        ))}
      </div>

      <div className="push_chat">
        <input
          value={inputText}
          onChange={(e) => setInputText(e.target.value)}
          placeholder="Введите сообщение"
          className="input_chat"
        />
        <button className="chat_button" onClick={sendMessage}>Отправить</button>
      </div>
    </div>
  );
}

export default Chat;
