import { useState, useEffect } from 'react';
import './Chat.css'
import ChatInfo from './Chat_info';
import Chat from './Chat';
import ReactDOM from 'react-dom/client';
import { useNavigate } from 'react-router-dom';

function Chats(){

    const navigate = useNavigate()

    const[search, setSearch] = useState('')
    const[searchCheck, setSearchCheck] = useState(true)
    const[isClick, setIsClick] = useState(false)
    const[userInfo, setUserInfo] = useState([{username:'', id:-1}])


    const handleChange = (e) => {
      setSearch(e.target.value)
      
    }

    async function refersh() {
      try {
        const response = await fetch("http://localhost:8080/auth/refresh", {
          method: "POST",
          credentials: "include", 
          headers: {
            "Content-Type": "application/json"
          }
        });

        if (response.ok) {
          
        } else {
          navigate('/')
        }
      } catch (error) {
        navigate('/')
      }
    }
    
    async function searchUser(){
      if(search.trim()==''){
        setSearchCheck(true)
      }
      else{
        setSearchCheck(false)
      }
      try {
      const response = await fetch(`http://localhost:8080/api/users/${search}`, {
        method: 'GET',
        headers: {
          'Authorization': `Bearer ${localStorage.getItem("token")}`,
          'Content-Type': 'application/json'
        },
      });

      if (response.ok) {
        alert("Круто")
        const data = await response.json();
        console.log(data)
        setUserInfo((prev) => [...prev, data])
        console.log(userInfo[0].username)
      } 
      else if (response.status == 401) {
        await refersh()
        if(response){
          await searchUser()
        }
      }
      else {
        alert('Ошибка при отправке данных');
        console.log(response.status)
      } 
      } catch (error) {
        console.error('Ошибка:', error);
      }
    }

    const [chooseChat, setchooseChat] = useState(0);
    function clickHandler(id){
        setchooseChat(id)
    }

    

    return(
        <div className='chats_block'>
            <div className="folder_block">
                <div className="user_block">
                    <div className="user_folder_photo"></div>
                    <input className="user_folder_search" placeholder='Поиск' value={search} onChange={handleChange}/>
                    <button onClick={searchUser}>src</button>
                </div>
                <div className="folder">
                    {/* {messangeinfo.map(item => 
                        <ChatInfo
                            name={item.name}
                            text = {item.text}
                            time={item.time}
                            img={item.img}
                            func={() => clickHandler(item.chatId)}
                        />
                    )} */}

                    {searchCheck ? 
                      // <ChatInfo
                      //     name={item.name}
                      //     text = {item.text}
                      //     time={item.time}
                      //     img={item.img}
                      //     func={() => clickHandler(item.chatId)}
                      // /> 
                      <div></div>
                      : 
                      <div>
                        <ChatInfo
                            name={userInfo[0].username}
                            text = {""}
                            time={""}
                            img={""}
                            func={() => clickHandler(userInfo[0].id)}
                          /> 
                      </div>
                    }
                </div>
            </div>
            <div className='chat_block'>

              {isClick ? <Chat id={chooseChat}/> : <div></div>}
                
            </div>
        </div>
    )
    
}

export default Chats;
